import Head from "next/head";
import { useState, useEffect } from "react";
import { defaultData } from "../config";
import { getData, getSearchParams } from "../controllers/fetchData";
import Form from "../components/form";


export default function Home() {
  const [campaignData, setCampaignData] = useState(defaultData);
  const [params, setParams] = useState(null);
  const [fectching, setFetching] = useState(true);
  const [showFormBox, setShowFormBox] = useState(false);

  useEffect(() => {
    getData(
      campaignData,
      setCampaignData,
      getSearchParams(params, setParams),
      setFetching
    );

    return () => {
      window.addEventListener("scroll", handleHeaderEffect);
    };
  }, [showHeaderEffect]);

  const handleHeaderEffect = (e) => {
    if (window.scrollY > 10 && !showHeaderEffect) {
      setShowHeaderEffect(true);
    } else if (window.scrollY < 10 && showHeaderEffect) {
      setShowHeaderEffect(false);
    }
  };

  const handlePopup = (popup, trackingType) => {
    if (popup === "exitPopup") {
      setShowExitPopup(trackingType);
    } else if (popup === "formBox") {
      setShowFormBox(trackingType);
    }
  };

  const handleClosePopup = () => {
    setShowFormBox(false);
    setShowPopup(false);
  };

  return (
    <div>
    <Form></Form>
    </div>
  );
}
